import {db } from "../firebase";
import { doc, deleteDoc, collection,addDoc, serverTimestamp } from "firebase/firestore";

export const newPurchases = async (data, r) =>{
    const purchase_name = data.p_name;
    const unit = data.unit_bought;
    const description = data.p_descr;
    const price = data.price;
    const paid = data.paid;
   const purchases_data = {purchase_name, unit, description, price, paid, timestamp: serverTimestamp()}
   const store_usage = {purchase_name,unit, timestamp: serverTimestamp()}

    try{

     const collectionRef = collection(db, "purchases");
     await addDoc(collectionRef, purchases_data);
     alert("Registration successfully");
     r.target.reset();
    }catch(err){
      console.log(err);
    }
   try{
    const collectionRef = collection(db, "storeUsage");
    await addDoc(collectionRef, store_usage);
   }catch(err){
    console.log(err);
   }

  };

export const handleDelete = async (id) =>{
    const docRef = doc(db, "purchases", id);
   const r = window.confirm("Press a button!");
   if (r === true){
    await deleteDoc(docRef);
   }


}
