import React,{useState} from 'react';
import { useForm } from "react-hook-form";
import Modal from "react-modal";
import { db } from "../../firebase";
import { collection,addDoc, serverTimestamp,} from "firebase/firestore";
import { 
    BsDownload,
    BsPlus,
    BsX
   } from "react-icons/bs";

function AddPurchases() {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const { register, handleSubmit, formState: { errors } } = useForm();

    const newPurchases = async (data, r) =>{
        const purchase_name = data.p_name;
        const unit = data.unit_bought;
        const description = data.p_descr;
        const price = data.price;
        const paid = data.paid;
       const purchases_data = {purchase_name, unit, description, price, paid, timestamp: serverTimestamp()}

        try{

         const collectionRef = collection(db, "purchases");
         await addDoc(collectionRef, purchases_data);
         alert("Registration successfully");
         r.target.reset();
        }catch(err){
          console.log(err);
        }

      };
    return (
        <>
            <div className="flex space-x-3 ">
                    <span className="bg-green-800  rounded-md shadow-2xl text-white font-extrabold px-2 py-2 text-2xl">
                    <BsDownload />
                    </span>

                      <span onClick={()=>{setModalIsOpen(true)}} className="bg-green-800 rounded-md shadow-xl text-white font-extrabold px-2 py-2 text-2xl"> 
                        <BsPlus />
                      </span>
                  </div>

              <Modal isOpen={modalIsOpen} className="w-1/2 ml-96 mt-36">
                  <form onSubmit={handleSubmit(newPurchases)}  class="w-full max-w-lg bg-gray-100 p-10 shadow-2xl rounded-md ml-24">
                        <div className="">
                            <BsX onClick={()=>setModalIsOpen(false)}  className="mb-3 text-2xl cursor-pointer font-bold ml-auto" />
                        </div>
                          <div class="flex flex-wrap -mx-3 mb-6">
                            <div class="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-first-name">
                                Purchase Name
                              </label>
                              <input  class="appearance-none block w-full  text-gray-700 rounded-lg border-2 border-gray-200 outline-none  pl-4 pr-3 py-2 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-first-name" type="text" placeholder="Purchase name" name="p_name" ref={register} />
                            </div>

                    <div class="w-full md:w-1/2 px-3">
                      <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-last-name">
                        Unit
                      </label>
                      <input  class="appearance-none block w-full  text-gray-700 rounded-lg border-2 border-gray-200 outline-none  pl-4 pr-3 py-2 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="text" placeholder="Unit" name="unit_bought" ref={register} />
                    </div>
                  </div>
                    <div className="flex flex-wrap -mx-3 mb-6">
                        <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                          <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-first-name">
                          Description
                          </label>
                          <textarea class="w-full px-3 py-2 text-gray-700 border rounded-lg focus:outline-none" rows="4" name="p_descr" ref={register} ></textarea>

                        </div>
                        <div className="w-full md:w-1/2 px-3">
                          <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-last-name">
                            Price
                          </label>
                                <input  className="appearance-none block w-full  text-gray-700 rounded-lg border-2 border-gray-200 outline-none pl-4 pr-3 py-2 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="text" placeholder="Price" name="price" ref={register} />
                        </div>
                    </div>

                      <div className="mt-2 mb-3">
                        <label className="inline-flex items-center">
                          <input type="radio" className="form-radio h-4 w-4" name="paid" value="Cash" ref={register} />
                          <span className="ml-2">Cash</span>
                        </label>
                        <label className="inline-flex items-center ml-6">
                          <input type="radio" className="form-radio h-4 w-4" name="paid" value="Credit" ref={register} />
                          <span className="ml-2">Credit</span>
                        </label>
                      </div>

                        {/* buttons */}
                    <div className="flex flex-wrap -mx-3 mb-6">
                      <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                        <input onClick={()=>setModalIsOpen(false)} className="appearance-none block w-full bg-red-500 text-white font-bold  rounded-md pr-3 py-3 mb-3 leading-tight focus:outline-none  " id="grid-first-name" type="button" value="Close" />
                      </div>
                      <div className="w-full md:w-1/2 px-3">
                        <input type="submit" class="appearance-none block w-full bg-green-500 text-white font-bold rounded-md  pr-3 py-3 leading-tight focus:outline-none  focus:border-gray-500" id="grid-last-name"  value="Submit"  />
                      </div>
                    </div>


                  </form>
               </Modal>

        </>
    )
}

export default AddPurchases
