import React,{useEffect,useState} from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import {db} from "../../firebase"
import { collection,onSnapshot, query, orderBy } from "firebase/firestore";

export default function LinePlot() {
  const [ total, setTotal ] = useState(0);
  const [ purchases, setPurchases ] = useState([]);
  useEffect(() =>{
    const q = query(collection(db,"purchases"),orderBy("timestamp","desc"))
    onSnapshot(q,(snapshot) =>{
      setPurchases(snapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id})));
    });
  },[]);

  const months = ['jan','feb','mar','apr','may','jun','jul','aug','sep','oct','nov','dec']

  const funnel = (index) =>{
    let m = 0
    const purchase_data = purchases.filter(purchase=> purchase.timestamp.toDate().getMonth()=== index)
    purchase_data.map(data=>(
      m+=parseInt(data.price)

    ))
    setTotal(m)

  }


    const data = [
      {
        name: 'Jan',
        pv: 2400,
        amt: 2400,
      },
      {
        name: 'Feb',
        pv: 1398,
        amt: 2210,
      },
      {
        name: 'Mar',
        pv: 9800,
        amt: 2290,
      },
      {
        name: 'Apr',
        pv: 3908,
        amt: 2000,
      },
      {
        name: 'May',
        pv: 4800,
        amt: 2181,
      },
      {
        name: 'Jun',
        pv: 3800,
        amt: 2500,
      },
      {
        name: 'Jul',
        pv: 4300,
        amt: 2100,
      },
      {
        name: 'Aug',
        pv: 3800,
        amt: 2500,
      },
      {
        name: 'Sep',
        pv: 9800,
        amt: 2290,
      },
      {
        name: 'Oct',
        pv: 4800,
        amt: 2181,
      },
      {
        name: 'Nov',
        pv: 4800,
        amt: 2181,
      },
      {
        name: 'Dec',
        pv: 3800,
        amt: 2500,
      },
    ];
    console.log(data[0].name)

  return (
    <>
      <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
        <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-blueGray-800">
          <div className="rounded-t mb-0 px-4 py-3 bg-white">
            <div className="flex flex-wrap items-center ">
              <div className="relative  w-full max-w-full flex-grow flex-1">
                <h6 className="uppercase text-white mb-1 text-xs font-semibold">
                  Overview
                </h6>
                <h2 className="text-gray-500 text-xl font-semibold">
                 Purchases
                </h2>
              </div>
            </div>
          </div>
          <div className="p-4 flex-auto bg-white">
            {/* Chart */}
            <div className="relative" style={{ height: "350px" }}>
            <ResponsiveContainer width="100%" height="100%">
        <LineChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />

        </LineChart>
      </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
