import {db } from "../firebase";
import { doc, deleteDoc } from "firebase/firestore";

export const handleDelete = async (id) =>{
    const docRef = doc(db, "purchases", id);
    window.confirm("Press a button!");
    await deleteDoc(docRef);

}
